import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Container,
  Box,
  Typography,
  Button,
  Card,
  CardContent,
  Paper,
} from '@mui/material';
import styled from 'styled-components';
import { useVan } from '../contexts/VanContext';

const StyledContainer = styled(Container)`
  height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
  background: linear-gradient(45deg, #1976D2 30%, #0D47A1 90%);
`;

const StyledCard = styled(Card)`
  text-align: center;
  padding: 32px;
  max-width: 500px;
  width: 100%;
`;

// Função simulada para gerar PDF - em um caso real, você usaria uma biblioteca como jspdf ou react-pdf
const generatePDF = (data: any) => {
  // Aqui você implementaria a geração real do PDF
  console.log('Gerando PDF com os dados:', data);
  
  // Simulação de download - em um caso real, você geraria o blob do PDF
  const element = document.createElement('a');
  const file = new Blob(['Conteúdo do PDF simulado'], { type: 'application/pdf' });
  element.href = URL.createObjectURL(file);
  element.download = 'carta_van.pdf';
  document.body.appendChild(element);
  element.click();
  document.body.removeChild(element);
};

function Success(): React.ReactElement {
  const navigate = useNavigate();
  const { vanData, resetVanData } = useVan();

  useEffect(() => {
    // Primeiro tenta usar os dados do contexto
    if (vanData.companyData) {
      generatePDF(vanData);
    } else {
      // Se não houver dados no contexto, tentar usar os dados do localStorage
      try {
        const savedData = localStorage.getItem('companyData');
        if (savedData) {
          const parsedData = JSON.parse(savedData);
          console.log('Usando dados do localStorage para gerar PDF:', parsedData);
          generatePDF({ companyData: parsedData, selectedBank: { name: parsedData.banco } });
        } else {
          console.error('Não há dados disponíveis para gerar o PDF');
          alert('Não há dados disponíveis para gerar o PDF. Por favor, preencha o formulário novamente.');
        }
      } catch (error) {
        console.error('Erro ao carregar dados do localStorage:', error);
      }
    }
  }, [vanData]);

  const handleReset = (): void => {
    try {
      // Limpar o localStorage ao reiniciar
      localStorage.removeItem('companyData');
      resetVanData();
    } catch (error) {
      console.error('Erro ao resetar dados:', error);
    }
    // Usar navegação direta por segurança
    window.location.href = '/';
  };

  const ticketNumber = '#456981';

  return (
    <StyledContainer>
      <StyledCard>
        <CardContent>
          <Typography variant="h5" gutterBottom>
            Parabéns! Sua carta de VAN foi criada com sucesso.
          </Typography>
          
          <Typography variant="body1" color="text.secondary" paragraph>
            Você receberá suas cartas em anexo via e-mail juntamente com o link do atendimento para acompanhar a abertura do relacionamento com o banco selecionado.
          </Typography>
          
          <Typography variant="body1" color="text.secondary" paragraph>
            Se houver qualquer dúvida, acesse nosso chat aqui.
          </Typography>

          <Box sx={{ mt: 3, mb: 3 }}>
            <Typography variant="h6" color="primary">
              {ticketNumber}
            </Typography>
            <Typography variant="body2" color="text.secondary">
              Link de acesso: https://www.zendesk.com.br/tecnospeed/456981
            </Typography>
          </Box>

          <Button
            variant="contained"
            onClick={handleReset}
            fullWidth
          >
            Início
          </Button>
        </CardContent>
      </StyledCard>
    </StyledContainer>
  );
}

export default Success;
