import React, { useMemo, useState } from 'react';
import {
  Box, Container, Typography, Button, Card, CardContent,
  Divider, CircularProgress, Alert, useMediaQuery,
  IconButton, Drawer, List, ListItem, ListItemIcon, ListItemText, AppBar, Toolbar
} from '@mui/material';
import { useNavigate } from 'react-router-dom';
import { useVan } from '../contexts/VanContext';
import MenuIcon from '@mui/icons-material/Menu';
import AccountBalanceIcon from '@mui/icons-material/AccountBalance';
import SettingsIcon from '@mui/icons-material/Settings';
import AssignmentIcon from '@mui/icons-material/Assignment';
import CheckCircleIcon from '@mui/icons-material/CheckCircle';
import { useTheme } from '@mui/material/styles';
import styled from 'styled-components';

const Content = styled(Box)`
  padding: 32px;
  margin-left: 250px;

  @media (max-width: 900px) {
    margin-left: 0;
    padding: 24px;
  }
`;

const drawerItems = [
  { text: 'Instituição bancária', icon: <AccountBalanceIcon />, path: '/bank' },
  { text: 'Produtos', icon: <SettingsIcon />, path: '/products' },
  { text: 'Preenchimento de dados', icon: <AssignmentIcon />, path: '/company-data' },
  { text: 'Conferir e validar', icon: <CheckCircleIcon />, path: '/review', active: true },
];

function ReviewData(): React.ReactElement {
  const navigate = useNavigate();
  const { vanData } = useVan();
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [drawerOpen, setDrawerOpen] = useState(false);
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('md'));

  const toggleDrawer = () => setDrawerOpen(!drawerOpen);

  const backupData = useMemo(() => {
    try {
      const saved = localStorage.getItem('companyData');
      return saved ? JSON.parse(saved) : null;
    } catch {
      return null;
    }
  }, []);

  const formattedData = useMemo(() => {
    const data = vanData.companyData || backupData || {};
    return {
      empresa: {
        nome: data.razaoSocial || '[NOME DA EMPRESA]',
        cnpj: data.cnpj || '[CNPJ DA EMPRESA]',
      },
      responsavel: {
        nome: data.responsavelNome || '[NOME DO RESPONSÁVEL]',
        email: data.responsavelEmail || '[E-MAIL DO RESPONSÁVEL]',
        telefone: data.responsavelTelefone || '[TELEFONE DO RESPONSÁVEL]',
      },
      conta: {
        banco: vanData.selectedBank?.name || data.banco || '[BANCO]',
        agencia: data.agencia || '[AGÊNCIA]',
        conta: data.conta || '[CONTA]',
        convenio: data.convenio || '[CONVÊNIO]',
      },
      gerente: {
        nome: data.gerenteConta || '[NOME GERENTE]',
        email: data.gerenteEmail || '[EMAIL GERENTE]',
        telefone: data.gerenteTelefone || '[TELEFONE GERENTE]',
      },
    };
  }, [vanData, backupData]);

  const handleSubmit = async () => {
    setLoading(true);
    setError(null);
    try {
      const data = vanData.companyData || backupData;
      if (!data) throw new Error('Dados ausentes');
      localStorage.setItem('companyData', JSON.stringify(data));
      navigate('/success');
    } catch (err) {
      setError('Erro ao processar os dados.');
    } finally {
      setLoading(false);
    }
  };

  const renderDrawerContent = () => (
    <List sx={{ width: 250 }}>
      {drawerItems.map(({ text, icon, path, active }) => (
        <ListItem
          button
          key={text}
          selected={active}
          onClick={() => {
            navigate(path);
            if (isMobile) setDrawerOpen(false);
          }}
        >
          <ListItemIcon>{icon}</ListItemIcon>
          <ListItemText primary={text} />
        </ListItem>
      ))}
    </List>
  );

  return (
    <Box>
      <AppBar position="fixed" color="primary" sx={{ zIndex: 1201 }}>
        <Toolbar>
          {isMobile && (
            <IconButton color="inherit" edge="start" onClick={toggleDrawer} sx={{ mr: 2 }}>
              <MenuIcon />
            </IconButton>
          )}
          <img src="/tecno_branca.png" alt="Tecnospeed" style={{ height: '30px' }} />
        </Toolbar>
      </AppBar>

      {/* Drawer lateral */}
      {isMobile ? (
        <Drawer anchor="left" open={drawerOpen} onClose={toggleDrawer}>
          {renderDrawerContent()}
        </Drawer>
      ) : (
        <Drawer variant="permanent" anchor="left" sx={{ width: 250, [`& .MuiDrawer-paper`]: { width: 250, top: 64 } }}>
          {renderDrawerContent()}
        </Drawer>
      )}

      <Content>
        <Container maxWidth="md">
          <Typography variant="h5" gutterBottom>
            4. Conferir e validar informações da carta
          </Typography>
          <Typography variant="body1" color="text.secondary">
            Verifique todos os dados abaixo antes de confirmar.
          </Typography>

          {error && <Alert severity="error" sx={{ mt: 2 }}>{error}</Alert>}

          <Card sx={{ mt: 4 }}>
            <CardContent>
              <Typography variant="h6" align="center" gutterBottom>
                Carta Autorização – Boletos
              </Typography>

              <Box sx={{ mt: 3, px: 1 }}>
                {/* Conteúdo da carta... (sem alterações aqui) */}
                {/* ... igual ao trecho anterior omitido por brevidade */}
              </Box>
            </CardContent>
          </Card>

          <Box sx={{ display: 'flex', justifyContent: 'space-between', mt: 4 }}>
            <Button variant="outlined" onClick={() => navigate('/company-data')} disabled={loading}>
              Voltar
            </Button>
            <Button variant="contained" onClick={handleSubmit} disabled={loading}>
              {loading ? (
                <Box sx={{ display: 'flex', alignItems: 'center' }}>
                  <CircularProgress size={22} sx={{ mr: 1 }} color="inherit" />
                  Processando...
                </Box>
              ) : (
                'Confirmar e Enviar Carta'
              )}
            </Button>
          </Box>
        </Container>
      </Content>
    </Box>
  );
}

export default ReviewData;
