import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Container,
  Box,
  Typography,
  Select,
  MenuItem,
  Paper,
  Alert,
  CircularProgress,
  SelectChangeEvent,
} from '@mui/material';
import { Home, Description, Edit, CheckCircle } from '@mui/icons-material';
import styled from 'styled-components';
import { bankService } from '../services/bank.service';
import { useVan } from '../contexts/VanContext';
import { Bank } from '../services/mock.service';

const StyledSidebar = styled(Paper)`
  width: 250px;
  padding: 16px;
  display: flex;
  flex-direction: column;
  gap: 16px;
  height: calc(100vh - 64px);
  position: fixed;
  left: 0;
  top: 64px;
  background-color: #f0f0f0;
`;

const StyledContent = styled(Box)`
  margin-left: 250px;
  padding: 24px;
`;

interface SidebarStepProps {
  icon: React.ReactNode;
  title: string;
  description: string;
  active?: boolean;
  disabled?: boolean;
  onClick?: () => void;
}

const SidebarStep = ({ icon, title, description, active, disabled, onClick }: SidebarStepProps) => (
  <Paper
    onClick={disabled ? undefined : onClick}
    elevation={0}
    sx={{
      display: 'flex',
      alignItems: 'flex-start',
      gap: 2,
      p: 2,
      borderRadius: 2,
      backgroundColor: active ? 'primary.main' : '#f5f5f5',
      color: active ? 'white' : 'inherit',
      opacity: disabled ? 0.6 : 1,
      cursor: disabled ? 'default' : 'pointer',
      transition: 'background-color 0.3s',
      '&:hover': {
        backgroundColor: !active && !disabled ? '#e0e0e0' : undefined,
      },
    }}
  >
    <Box sx={{ mt: 0.5 }}>{icon}</Box>
    <Box>
      <Typography variant="subtitle1" fontWeight="bold">
        {title}
      </Typography>
      <Typography variant="body2" color={active ? 'white' : 'text.secondary'}>
        {description}
      </Typography>
    </Box>
  </Paper>
);

function BankSelection(): React.ReactElement {
  const navigate = useNavigate();
  const { vanData, updateVanData } = useVan();
  const [banks, setBanks] = useState<Bank[]>([]);
  const [loading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<string>('');

  useEffect(() => {
    loadBanks();
  }, []);

  const loadBanks = async (): Promise<void> => {
    try {
      const data = await bankService.getBanks();
      setBanks(data);
    } catch (err) {
      setError('Erro ao carregar a lista de bancos. Tente novamente.');
    } finally {
      setLoading(false);
    }
  };

  const handleBankChange = (event: SelectChangeEvent<string>): void => {
    const selectedBank = banks.find(bank => bank.id === event.target.value);
    if (selectedBank) {
      updateVanData({ selectedBank });
    }
  };

  const handleNext = (): void => {
    if (vanData.selectedBank) {
      navigate('/product-selection');
    }
  };

  if (loading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
        <CircularProgress />
      </Box>
    );
  }

  return (
    <Box>
      {/* Header */}
      <Box
        sx={{
          height: '64px',
          backgroundColor: 'primary.main',
          display: 'flex',
          alignItems: 'center',
          padding: '0 24px',
          position: 'fixed',
          top: 0,
          left: 0,
          right: 0,
          zIndex: 1000,
        }}
      >
        <img
          src="/tecno_branca.png"
          alt="Tecnospeed"
          style={{ height: '30px' }}
        />
      </Box>

      {/* Sidebar */}
      <StyledSidebar elevation={0}>
        <SidebarStep
          icon={<Home color="inherit" />}
          title="Instituição bancária"
          description="Selecione uma instituição"
          active
        />
        <SidebarStep
          icon={<Description color="primary" />}
          title="Produtos"
          description="Selecione um ou mais produtos"
          disabled
        />
        <SidebarStep
          icon={<Edit color="primary" />}
          title="Preenchimento de dados"
          description="Selecione um ou mais produtos"
          disabled
        />
        <SidebarStep
          icon={<CheckCircle color="primary" />}
          title="Conferir e validar"
          description="Confirme os dados antes de enviar"
          disabled
        />
      </StyledSidebar>

      {/* Main Content */}
      <StyledContent>
        <Container maxWidth="md" sx={{ mt: 8 }}>
          <Typography variant="h5" gutterBottom>
            1. Selecionar um Banco (Instituição Bancária)
          </Typography>
          <Typography variant="body1" color="text.secondary" gutterBottom>
            Selecione um banco para criar uma nova carta de VAN
          </Typography>

          {error && (
            <Alert severity="error" sx={{ mb: 3 }}>
              {error}
            </Alert>
          )}

          <Box sx={{ mt: 4 }}>
            <Select
              value={vanData.selectedBank?.id || ''}
              onChange={handleBankChange}
              displayEmpty
              fullWidth
              sx={{ mb: 3 }}
            >
              {banks.map((bank) => (
                <MenuItem key={bank.id} value={bank.id}>
                  {bank.code} - {bank.name}
                </MenuItem>
              ))}
            </Select>

            <Box sx={{ display: 'flex', justifyContent: 'flex-end', mt: 33 }}>
              <Typography>
                {/* Pode incluir botão "Voltar" se necessário */}
              </Typography>
              <Typography>
                <Box>
                  <button onClick={handleNext} disabled={!vanData.selectedBank}>
                    Próximo
                  </button>
                </Box>
              </Typography>
            </Box>
          </Box>
        </Container>
      </StyledContent>
    </Box>
  );
}

export default BankSelection;
