import React, { createContext, useContext, useState } from 'react';

const VanContext = createContext();

export function VanProvider({ children }) {
  const [vanData, setVanData] = useState({
    selectedBank: null,
    selectedProducts: [],
    companyData: null,
    letterType: null,
  });

  const updateVanData = (newData) => {
    setVanData((prev) => ({
      ...prev,
      ...newData,
    }));
  };

  const resetVanData = () => {
    setVanData({
      selectedBank: null,
      selectedProducts: [],
      companyData: null,
      letterType: null,
    });
  };

  return (
    <VanContext.Provider value={{ vanData, updateVanData, resetVanData }}>
      {children}
    </VanContext.Provider>
  );
}

export function useVan() {
  const context = useContext(VanContext);
  if (!context) {
    throw new Error('useVan must be used within a VanProvider');
  }
  return context;
}
