import React, { useState, ChangeEvent, FormEvent, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Container,
  Box,
  Typography,
  TextField,
  Button,
  Paper,
  Grid,
} from '@mui/material';
import styled from 'styled-components';
import { useVan } from '../contexts/VanContext';

interface FormDataType {
  cnpj: string;
  razaoSocial: string;
  responsavelNome: string;
  responsavelCargo: string;
  responsavelTelefone: string;
  responsavelEmail: string;
  banco: string;
  agencia: string;
  conta: string;
  convenio: string;
  gerenteConta: string;
  gerenteTelefone: string;
  gerenteEmail: string;
}

interface StepButtonProps {
  active?: boolean;
}

const StyledSidebar = styled(Paper)`
  width: 250px;
  padding: 16px;
  display: flex;
  flex-direction: column;
  gap: 8px;
  height: calc(100vh - 64px);
  position: fixed;
  left: 0;
  top: 64px;
`;

const StyledContent = styled(Box)`
  margin-left: 250px;
  padding: 24px;
`;

const StepButton = styled(Button)<StepButtonProps>`
  justify-content: flex-start;
  text-align: left;
  padding: 16px;
  background-color: ${(props) => (props.active ? '#e3f2fd' : 'transparent')};
  &:hover {
    background-color: #e3f2fd;
  }
`;

function CompanyData(): React.ReactElement {
  const navigate = useNavigate();
  const { vanData, updateVanData } = useVan();

  const [formData, setFormData] = useState<FormDataType>({
    cnpj: '',
    razaoSocial: '',
    responsavelNome: '',
    responsavelCargo: '',
    responsavelTelefone: '',
    responsavelEmail: '',
    banco: vanData.selectedBank?.name || '',
    agencia: '',
    conta: '',
    convenio: '',
    gerenteConta: '',
    gerenteTelefone: '',
    gerenteEmail: '',
  });

  useEffect(() => {
    if (vanData.companyData) {
      setFormData({
        ...formData,
        ...vanData.companyData,
        banco: vanData.selectedBank?.name || '',
      });
    }
  }, []);

  const handleChange = (e: ChangeEvent<HTMLInputElement>): void => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleSubmit = (e: FormEvent<HTMLFormElement>): void => {
    e.preventDefault();

    updateVanData({
      companyData: formData,
    });

    navigate('/review');
  };

  const isFormValid = (): boolean => {
    const requiredFields = [
      'cnpj',
      'razaoSocial',
      'responsavelNome',
      'responsavelCargo',
      'responsavelTelefone',
      'responsavelEmail',
      'agencia',
      'conta',
      'convenio',
      'gerenteConta',
      'gerenteTelefone',
      'gerenteEmail',
    ];

    return requiredFields.every(
      (field) => formData[field as keyof FormDataType]?.trim() !== ''
    );
  };

  return (
    <Box>
      {/* Header */}
      <Box
        sx={{
          height: '64px',
          backgroundColor: 'primary.main',
          display: 'flex',
          alignItems: 'center',
          padding: '0 24px',
          position: 'fixed',
          top: 0,
          left: 0,
          right: 0,
          zIndex: 1000,
        }}
      >
        <img src="/tecno_branca.png" alt="Tecnospeed" style={{ height: '30px' }} />
      </Box>

      {/* Sidebar */}
      <StyledSidebar elevation={1}>
        <StepButton variant="text" color="primary">
          Instituição bancária
        </StepButton>
        <StepButton variant="text" color="primary">
          Produtos
        </StepButton>
        <StepButton active variant="text" color="primary">
          Preenchimento de dados
        </StepButton>
        <StepButton variant="text" disabled>
          Conferir e validar
        </StepButton>
      </StyledSidebar>

      {/* Main Content */}
      <StyledContent>
        <Container maxWidth="md" sx={{ mt: 8 }}>
          {/* ...restante do conteúdo do formulário */}
          {/* Seu formulário e lógica continuam aqui */}
        </Container>
      </StyledContent>
    </Box>
  );
}

export default CompanyData;
