import React from 'react';
import { Box, Typography } from '@mui/material';
import styled from 'styled-components';
import { useNavigate } from 'react-router-dom';

// Styled components
const StyledSidebar = styled(Box)`
  width: 300px;
  padding: 20px;
  display: flex;
  flex-direction: column;
  align-items: center;
  height: calc(100vh - 64px);
  position: fixed;
  left: 0;
  top: 64px;
  background-color: #f5f5f5;
`;

const StepsWrapper = styled(Box)`
  background-color: #ffffff;
  border-radius: 16px;
  padding: 20px;
  width: 100%;
  display: flex;
  flex-direction: column;
  gap: 16px;
`;

const StepCard = styled(Box)<{ active?: boolean }>`
  border-radius: 12px;
  padding: 16px;
  border: 1px solid ${({ active }) => (active ? '#004b80' : '#ddd')};
  background-color: ${({ active }) => (active ? '#004b80' : '#ffffff')};
  transition: background-color 0.3s, border 0.3s;
  cursor: ${({ active }) => (active ? 'default' : 'pointer')};
`;

const Sidebar: React.FC = () => {
  const navigate = useNavigate();

  const steps = [
    {
      label: 'Instituição bancária',
      sublabel: 'Selecione uma instituição',
      path: '/bank-selection',
      step: 1,
    },
    {
      label: 'Produtos',
      sublabel: 'Selecione um ou mais produtos',
      path: '/product-selection',
      step: 2,
    },
    {
      label: 'Preenchimento de dados',
      sublabel: 'Preencha os dados solicitados',
      path: '',
      step: 3,
    },
    {
      label: 'Conferir e validar',
      sublabel: 'Confirme os dados antes de enviar',
      step: 4,
    },
  ];

  const activeStep = 3; // Você pode tornar isso dinâmico conforme o passo atual

  return (
    <StyledSidebar>
      <StepsWrapper>
        {steps.map((item, index) => {
          const isActive = item.step === activeStep;

          return (
            <StepCard
              key={index}
              active={isActive}
              onClick={() => item.path && navigate(item.path)}
            >
              <Typography
                variant="subtitle1"
                fontWeight={isActive ? 'bold' : 'medium'}
                sx={{ color: isActive ? '#ffffff' : '#444444' }}
              >
                {item.label}
              </Typography>
              <Typography
                variant="body2"
                sx={{ color: isActive ? '#d0e6ff' : '#aaaaaa' }}
              >
                {item.sublabel}
              </Typography>
            </StepCard>
          );
        })}
      </StepsWrapper>
    </StyledSidebar>
  );
};

export default Sidebar;
