import React, { useState, ChangeEvent, FormEvent, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Container,
  Box,
  Typography,
  TextField,
  Button,
  Grid,
  Paper,
} from '@mui/material';
import styled from 'styled-components';
import { useVan } from '../contexts/VanContext';

interface FormDataType {
  cnpj: string;
  razaoSocial: string;
  responsavelNome: string;
  responsavelCargo: string;
  responsavelTelefone: string;
  responsavelEmail: string;
  banco: string;
  agencia: string;
  agenciaDv: string;
  conta: string;
  contaDv: string;
  convenio: string;
  cnab: string;
  gerenteConta: string;
  gerenteTelefone: string;
  gerenteEmail: string;
}

const StyledSidebar = styled(Paper)`
  width: 280px;
  height: 100vh;
  position: fixed;
  top: 64px;
  left: 0;
  padding: 24px 16px;
  background-color: #f7f9fc;
  box-shadow: 2px 0 6px rgba(0, 0, 0, 0.08);
  display: flex;
  flex-direction: column;
  gap: 16px;
  z-index: 10;
`;

const StyledContent = styled(Box)`
  margin-left: 280px;
  padding: 32px;
`;

const StepButton = styled.div<{ active?: boolean }>`
  padding: 12px 16px;
  border-radius: 8px;
  background-color: ${({ active }) => (active ? '#003f71' : '#ffffff')};
  color: ${({ active }) => (active ? '#ffffff' : '#333')};
  border: 1px solid ${({ active }) => (active ? '#003f71' : '#ddd')};
  cursor: ${({ active }) => (active ? 'default' : 'pointer')};
  opacity: ${({ active }) => (active === false ? 0.7 : 1)};
  transition: all 0.3s ease;
  pointer-events: ${({ active }) => (active === false ? 'none' : 'auto')};

  &:hover {
    background-color: ${({ active }) => (active ? '#003f71' : '#f0f0f0')};
  }

  h6 {
    margin: 0;
    font-weight: 600;
  }

  p {
    margin: 4px 0 0;
    font-size: 0.85rem;
  }
`;

function CompanyData(): React.ReactElement {
  const navigate = useNavigate();
  const { vanData, updateVanData } = useVan();

  const [formData, setFormData] = useState<FormDataType>({
    cnpj: '',
    razaoSocial: '',
    responsavelNome: '',
    responsavelCargo: '',
    responsavelTelefone: '',
    responsavelEmail: '',
    banco: vanData.selectedBank?.name || '',
    agencia: '',
    agenciaDv: '',
    conta: '',
    contaDv: '',
    convenio: '',
    cnab: '',
    gerenteConta: '',
    gerenteTelefone: '',
    gerenteEmail: '',
  });

  useEffect(() => {
    if (vanData.companyData) {
      setFormData({
        ...formData,
        ...vanData.companyData,
        banco: vanData.selectedBank?.name || '',
      });
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  const handleChange = (e: ChangeEvent<HTMLInputElement>): void => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleSubmit = (e: FormEvent<HTMLFormElement>): void => {
    e.preventDefault();

    updateVanData({
      companyData: formData,
    });

    navigate('/review');
  };

  const isFormValid = (): boolean => {
    const requiredFields = [
      'cnpj',
      'razaoSocial',
      'responsavelNome',
      'responsavelCargo',
      'responsavelTelefone',
      'responsavelEmail',
      'agencia',
      'conta',
      'convenio',
      'gerenteConta',
      'gerenteTelefone',
      'gerenteEmail',
    ];

    return requiredFields.every(
      (field) => formData[field as keyof FormDataType]?.trim() !== ''
    );
  };

  return (
    <Box>
      {/* Header */}
      <Box
        sx={{
          height: '64px',
          backgroundColor: 'primary.main',
          display: 'flex',
          alignItems: 'center',
          padding: '0 24px',
          position: 'fixed',
          top: 0,
          left: 0,
          right: 0,
          zIndex: 1000,
        }}
      >
        <img src="/tecno_branca.png" alt="Tecnospeed" style={{ height: '30px' }} />
      </Box>

      {/* Sidebar */}
      <StyledSidebar elevation={1}>
        <StepButton onClick={() => navigate('/bank-selection')}>
          <Typography variant="subtitle1">1. Instituição bancária</Typography>
          <Typography variant="body2">Selecione uma instituição</Typography>
        </StepButton>

        <StepButton onClick={() => navigate('/product-selection')}>
          <Typography variant="subtitle1">2. Produtos</Typography>
          <Typography variant="body2">Escolha os produtos desejados</Typography>
        </StepButton>

        <StepButton active>
          <Typography variant="subtitle1">3. Dados da empresa</Typography>
          <Typography variant="body2">Preencha os dados solicitados</Typography>
        </StepButton>

        <StepButton>
          <Typography variant="subtitle1">4. Conferir e validar</Typography>
          <Typography variant="body2">Confirme as informações</Typography>
        </StepButton>
      </StyledSidebar>

      {/* Conteúdo */}
      <StyledContent>
        <Container maxWidth="md">
          <Typography variant="h5" gutterBottom>
            3. Preencher dados da empresa e conta
          </Typography>
          <Typography variant="body1" color="text.secondary">
            Precisamos desses dados para elaborar a carta de VAN.
          </Typography>

          <Box component="form" onSubmit={handleSubmit} sx={{ mt: 4 }}>
            {/* Empresa */}
            <Typography variant="h6" gutterBottom sx={{ mt: 3 }}>
              Empresa
            </Typography>
            <Grid container spacing={2}>
              <Grid item xs={12} sm={6}>
                <TextField
                  required
                  fullWidth
                  label="CNPJ"
                  name="cnpj"
                  value={formData.cnpj}
                  onChange={handleChange}
                />
              </Grid>
              <Grid item xs={12} sm={6}>
                <TextField
                  required
                  fullWidth
                  label="Razão Social"
                  name="razaoSocial"
                  value={formData.razaoSocial}
                  onChange={handleChange}
                />
              </Grid>
            </Grid>

            {/* Responsável */}
            <Typography variant="h6" gutterBottom sx={{ mt: 3 }}>
              Responsável pela Empresa
            </Typography>
            <Grid container spacing={2}>
              <Grid item xs={12} sm={6}>
                <TextField
                  required
                  fullWidth
                  label="Nome"
                  name="responsavelNome"
                  value={formData.responsavelNome}
                  onChange={handleChange}
                />
              </Grid>
              <Grid item xs={12} sm={6}>
                <TextField
                  required
                  fullWidth
                  label="Cargo"
                  name="responsavelCargo"
                  value={formData.responsavelCargo}
                  onChange={handleChange}
                />
              </Grid>
              <Grid item xs={12} sm={6}>
                <TextField
                  required
                  fullWidth
                  label="Telefone"
                  name="responsavelTelefone"
                  value={formData.responsavelTelefone}
                  onChange={handleChange}
                />
              </Grid>
              <Grid item xs={12} sm={6}>
                <TextField
                  required
                  fullWidth
                  label="E-mail"
                  name="responsavelEmail"
                  type="email"
                  value={formData.responsavelEmail}
                  onChange={handleChange}
                />
              </Grid>
            </Grid>

            {/* Conta */}
            <Typography variant="h6" gutterBottom sx={{ mt: 3 }}>
              Conta
            </Typography>
            <Grid container spacing={2}>
              <Grid item xs={12} sm={6}>
                <TextField
                  required
                  fullWidth
                  label="Agência"
                  name="agencia"
                  value={formData.agencia}
                  onChange={handleChange}
                />
              </Grid>
              <Grid item xs={12} sm={6}>
                <TextField
                  required
                  fullWidth
                  label="Conta"
                  name="conta"
                  value={formData.conta}
                  onChange={handleChange}
                />
              </Grid>
              <Grid item xs={12}>
                <TextField
                  required
                  fullWidth
                  label="Convênio"
                  name="convenio"
                  value={formData.convenio}
                  onChange={handleChange}
                />
              </Grid>
            </Grid>

            {/* Gerente */}
            <Typography variant="h6" gutterBottom sx={{ mt: 3 }}>
              Gerente da Conta
            </Typography>
            <Grid container spacing={2}>
              <Grid item xs={12} sm={6}>
                <TextField
                  required
                  fullWidth
                  label="Nome"
                  name="gerenteConta"
                  value={formData.gerenteConta}
                  onChange={handleChange}
                />
              </Grid>
              <Grid item xs={12} sm={6}>
                <TextField
                  required
                  fullWidth
                  label="Telefone"
                  name="gerenteTelefone"
                  value={formData.gerenteTelefone}
                  onChange={handleChange}
                />
              </Grid>
              <Grid item xs={12}>
                <TextField
                  required
                  fullWidth
                  label="E-mail"
                  name="gerenteEmail"
                  type="email"
                  value={formData.gerenteEmail}
                  onChange={handleChange}
                />
              </Grid>
            </Grid>

            {/* Botões */}
            <Box sx={{ display: 'flex', justifyContent: 'space-between', mt: 4 }}>
              <Button variant="outlined" onClick={() => navigate('/product-selection')}>
                Voltar
              </Button>
              <Button type="submit" variant="contained" disabled={!isFormValid()}>
                Revisar
              </Button>
            </Box>
          </Box>
        </Container>
      </StyledContent>
    </Box>
  );
}

export default CompanyData;
