import React from 'react';
import { Box, Typography } from '@mui/material';
import styled from 'styled-components';
import { useNavigate } from 'react-router-dom';

// Styled Components
const Container = styled(Box)`
  display: flex;
  height: 100vh;
`;

const Sidebar = styled(Box)`
  width: 300px;
  background-color: #f5f5f5;
  padding: 20px;
  box-sizing: border-box;
`;

const StepsWrapper = styled(Box)`
  background-color: #ffffff;
  border-radius: 16px;
  padding: 20px;
  display: flex;
  flex-direction: column;
  gap: 16px;
`;

const StepCard = styled(Box)<{ active?: boolean }>`
  border-radius: 12px;
  padding: 16px;
  border: 1px solid ${({ active }) => (active ? '#004b80' : '#ddd')};
  background-color: ${({ active }) => (active ? '#004b80' : '#ffffff')};
  transition: background-color 0.3s, border 0.3s;
  cursor: ${({ active }) => (active ? 'default' : 'pointer')};
`;

const Content = styled(Box)`
  flex: 1;
  padding: 40px;
  background-color: #f9f9f9;
`;

const LayoutPage: React.FC = () => {
  const navigate = useNavigate();

  const steps = [
    {
      label: 'Instituição bancária',
      sublabel: 'Selecione uma instituição',
      path: '/bank-selection',
      step: 1,
    },
    {
      label: 'Produtos',
      sublabel: 'Selecione um ou mais produtos',
      path: '/product-selection',
      step: 2,
    },
    {
      label: 'Preenchimento de dados',
      sublabel: 'Preencha os dados solicitados',
      path: '',
      step: 3,
    },
    {
      label: 'Conferir e validar',
      sublabel: 'Confirme os dados antes de enviar',
      step: 4,
    },
  ];

  const activeStep = 3;

  return (
    <Container>
      <Sidebar>
        <StepsWrapper>
          {steps.map((item, index) => {
            const isActive = item.step === activeStep;

            return (
              <StepCard
                key={index}
                active={isActive}
                onClick={() => item.path && navigate(item.path)}
              >
                <Typography
                  variant="subtitle1"
                  fontWeight={isActive ? 'bold' : 'medium'}
                  sx={{ color: isActive ? '#ffffff' : '#444444' }}
                >
                  {item.label}
                </Typography>
                <Typography
                  variant="body2"
                  sx={{ color: isActive ? '#d0e6ff' : '#aaaaaa' }}
                >
                  {item.sublabel}
                </Typography>
              </StepCard>
            );
          })}
        </StepsWrapper>
      </Sidebar>

      <Content>
        <Typography variant="h5" fontWeight="bold" gutterBottom>
          Conteúdo principal da etapa 3
        </Typography>
        <Typography>
          Aqui você pode colocar o formulário ou as informações da etapa atual.
        </Typography>
      </Content>
    </Container>
  );
};

export default LayoutPage;
