import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Container,
  Box,
  Typography,
  Select,
  MenuItem,
  Button,
  Paper,
  Alert,
  CircularProgress,
  SelectChangeEvent,
} from '@mui/material';
import styled from 'styled-components';
import HomeIcon from '@mui/icons-material/Home';
import DescriptionIcon from '@mui/icons-material/Description';
import EditNoteIcon from '@mui/icons-material/EditNote';
import FactCheckIcon from '@mui/icons-material/FactCheck';
import { bankService } from '../services/bank.service';
import { useVan } from '../contexts/VanContext';
import { Bank } from '../services/mock.service';

interface StepButtonProps {
  active?: boolean;
}

const StyledSidebar = styled(Paper)`
  width: 250px;
  padding: 16px;
  display: flex;
  flex-direction: column;
  gap: 16px;
  height: calc(100vh - 64px);
  position: fixed;
  left: 0;
  top: 64px;
  background-color: #f5f5f5;
`;

const StyledContent = styled(Box)`
  margin-left: 250px;
  padding: 24px;
`;

const StepCard = styled(Button)<StepButtonProps>`
  display: flex;
  flex-direction: row;
  align-items: flex-start;
  text-align: left;
  justify-content: flex-start;
  padding: 16px;
  border-radius: 12px;
  background-color: ${({ active }) => (active ? '#1E4C91' : '#e0e0e0')};
  color: ${({ active }) => (active ? '#fff' : '#000')};
  gap: 12px;
  box-shadow: none;
  &:hover {
    background-color: ${({ active }) => (active ? '#1E4C91' : '#d5d5d5')};
  }
`;

const StepTexts = styled(Box)`
  display: flex;
  flex-direction: column;
`;

const StepTitle = styled(Typography)`
  font-weight: bold;
  font-size: 14px;
`;

const StepSubtitle = styled(Typography)`
  font-size: 12px;
  color: #888;
`;

function BankSelection(): React.ReactElement {
  const navigate = useNavigate();
  const { vanData, updateVanData } = useVan();
  const [banks, setBanks] = useState<Bank[]>([]);
  const [loading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<string>('');

  useEffect(() => {
    loadBanks();
  }, []);

  const loadBanks = async (): Promise<void> => {
    try {
      const data = await bankService.getBanks();
      setBanks(data);
    } catch (err) {
      setError('Erro ao carregar a lista de bancos. Tente novamente.');
    } finally {
      setLoading(false);
    }
  };

  const handleBankChange = (event: SelectChangeEvent<string>): void => {
    const selectedBank = banks.find(bank => bank.id === event.target.value);
    if (selectedBank) {
      updateVanData({ selectedBank });
    }
  };

  const handleNext = (): void => {
    if (vanData.selectedBank) {
      navigate('/product-selection');
    }
  };

  if (loading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
        <CircularProgress />
      </Box>
    );
  }

  return (
    <Box>
      {/* Header */}
      <Box
        sx={{
          height: '64px',
          backgroundColor: 'primary.main',
          display: 'flex',
          alignItems: 'center',
          padding: '0 24px',
          position: 'fixed',
          top: 0,
          left: 0,
          right: 0,
          zIndex: 1000,
        }}
      >
        <img
          src="/tecno_branca.png"
          alt="Tecnospeed"
          style={{ height: '30px' }}
        />
      </Box>

      {/* Sidebar */}
      <StyledSidebar elevation={1}>
        <StepCard active startIcon={<HomeIcon />}>
          <StepTexts>
            <StepTitle>Instituição bancária</StepTitle>
            <StepSubtitle>Selecione uma instituição</StepSubtitle>
          </StepTexts>
        </StepCard>

        <StepCard disabled startIcon={<DescriptionIcon />}>
          <StepTexts>
            <StepTitle>Produtos</StepTitle>
            <StepSubtitle>Selecione um ou mais produtos</StepSubtitle>
          </StepTexts>
        </StepCard>

        <StepCard disabled startIcon={<EditNoteIcon />}>
          <StepTexts>
            <StepTitle>Preenchimento de dados</StepTitle>
            <StepSubtitle>Selecione um ou mais produtos</StepSubtitle>
          </StepTexts>
        </StepCard>

        <StepCard disabled startIcon={<FactCheckIcon />}>
          <StepTexts>
            <StepTitle>Conferir e validar</StepTitle>
            <StepSubtitle>Confirme os dados antes de enviar</StepSubtitle>
          </StepTexts>
        </StepCard>
      </StyledSidebar>

      {/* Main Content */}
      <StyledContent>
        <Container maxWidth="md" sx={{ mt: 8 }}>
          <Typography variant="h5" gutterBottom>
            1. Selecionar um Banco (Instituição Bancária)
          </Typography>
          <Typography variant="body1" color="text.secondary" gutterBottom>
            Selecione um banco para criar uma nova carta de VAN
          </Typography>

          {error && (
            <Alert severity="error" sx={{ mb: 3 }}>
              {error}
            </Alert>
          )}

          <Box sx={{ mt: 4 }}>
            <Select
              value={vanData.selectedBank?.id || ''}
              onChange={handleBankChange}
              displayEmpty
              fullWidth
              sx={{ mb: 3 }}
            >
              {banks.map((bank) => (
                <MenuItem key={bank.id} value={bank.id}>
                  {bank.code} - {bank.name}
                </MenuItem>
              ))}
            </Select>

            <Box sx={{ display: 'flex', justifyContent: 'flex-end', mt: 33 }}>
              <Button
                variant="contained"
                onClick={handleNext}
                disabled={!vanData.selectedBank}
              >
                Próximo
              </Button>
            </Box>
          </Box>
        </Container>
      </StyledContent>
    </Box>
  );
}

export default BankSelection;
