import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Container,
  Box,
  Typography,
  Select,
  MenuItem,
  Button,
  Paper,
  Alert,
  CircularProgress,
  SelectChangeEvent,
  AppBar,
  Toolbar,
  IconButton,
} from '@mui/material';
import MenuIcon from '@mui/icons-material/Menu';
import styled from 'styled-components';
import { bankService } from '../services/bank.service';
import { useVan } from '../contexts/VanContext';
import { Bank } from '../services/mock.service';

interface StepButtonProps {
  active?: boolean;
}

// Estilo da sidebar responsiva para mobile (drawer ou abaixo do header)
const StyledSidebarMobile = styled(Paper)`
  width: 100%;
  padding: 16px;
  display: flex;
  flex-direction: row;
  gap: 8px;
  overflow-x: auto; /* Permite scroll horizontal em telas muito pequenas */
  background-color: #f5f5f5;
  border-bottom: 1px solid #ddd;

  @media (min-width: 961px) {
    display: none; /* Oculta a sidebar mobile em telas maiores */
  }
`;

// Estilo dos botões de etapa na sidebar mobile
const StepButtonMobile = styled(Box)<StepButtonProps>`
  padding: 8px 12px;
  border-radius: 20px;
  border: 1px solid #ddd;
  background-color: ${({ active }) => (active ? '#003865' : '#fff')};
  color: ${({ active }) => (active ? '#fff' : '#888')};
  font-size: 0.8rem;
  white-space: nowrap; /* Evita que o texto quebre a linha */
`;

// Estilo da sidebar para desktop (fixa à esquerda)
const StyledSidebarDesktop = styled(Paper)`
  width: 250px; /* Reduzi um pouco a largura para mobile */
  padding: 16px;
  display: flex;
  flex-direction: column;
  gap: 10px;
  height: calc(100vh - 64px);
  position: fixed;
  left: 0;
  top: 64px;
  background-color: #f5f5f5;
  z-index: 1100;

  @media (max-width: 960px) {
    display: none; /* Oculta a sidebar desktop em telas menores */
  }
`;

// Estilo dos botões de etapa na sidebar desktop
const StepButtonDesktop = styled(Box)<StepButtonProps>`
  padding: 12px;
  border-radius: 8px;
  background-color: ${({ active }) => (active ? '#003865' : '#fff')};
  color: ${({ active }) => (active ? '#fff' : '#888')};
  font-weight: ${({ active }) => (active ? 'bold' : 'medium')};
  cursor: default;
`;

// Estilo do conteúdo principal responsivo
const StyledContent = styled(Box)`
  margin-left: 250px; /* Corresponde à largura da sidebar desktop */
  padding: 16px;
  padding-top: 80px; /* Espaço abaixo do header */
  min-height: calc(100vh - 64px);

  @media (max-width: 960px) {
    margin-left: 0;
    padding-top: 140px; /* Espaço para a sidebar mobile */
  }

  @media (max-width: 600px) {
    padding-top: 120px;
  }
`;

function BankSelection(): React.ReactElement {
  const navigate = useNavigate();
  const { vanData, updateVanData } = useVan();
  const [banks, setBanks] = useState<Bank[]>([]);
  const [loading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<string>('');
  const [mobileSidebarOpen, setMobileSidebarOpen] = useState(false);

  useEffect(() => {
    loadBanks();
  }, []);

  const loadBanks = async (): Promise<void> => {
    try {
      const data = await bankService.getBanks();
      setBanks(data);
    } catch (err) {
      setError('Erro ao carregar a lista de bancos. Tente novamente.');
    } finally {
      setLoading(false);
    }
  };

  const handleBankChange = (event: SelectChangeEvent<string>): void => {
    const selectedBank = banks.find(bank => bank.id === event.target.value);
    if (selectedBank) {
      updateVanData({ selectedBank });
    }
  };

  const handleNext = (): void => {
    if (vanData.selectedBank) {
      navigate('/product-selection');
    }
  };

  if (loading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
        <CircularProgress />
      </Box>
    );
  }

  const steps = [
    { label: 'Instituição bancária', sublabel: 'Selecione uma instituição', step: 1 },
    { label: 'Produtos', sublabel: 'Selecione um ou mais produtos', step: 2 },
    { label: 'Preenchimento de dados', sublabel: 'Preencha os dados solicitados', step: 3 },
    { label: 'Conferir e validar', sublabel: 'Confirme os dados antes de enviar', step: 4 },
  ];

  return (
    <Box>
      {/* Header */}
      <AppBar position="fixed">
        <Toolbar>
          <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
            Tecnospeed
          </Typography>
          <Box sx={{ display: { xs: 'block', md: 'none' } }}>
            <IconButton
              size="large"
              edge="end"
              color="inherit"
              aria-label="menu"
              onClick={() => setMobileSidebarOpen(!mobileSidebarOpen)}
            >
              <MenuIcon />
            </IconButton>
          </Box>
        </Toolbar>
      </AppBar>

      {/* Sidebar Mobile */}
      <StyledSidebarMobile sx={{ ...(mobileSidebarOpen ? { top: '64px' } : { top: '-100%' }), position: 'fixed', transition: 'top 0.3s ease-in-out', zIndex: 1200 }}>
        {steps.map((step) => (
          <StepButtonMobile key={step.step} active={step.step === 1}>
            {step.label}
          </StepButtonMobile>
        ))}
      </StyledSidebarMobile>

      {/* Sidebar Desktop */}
      <StyledSidebarDesktop elevation={1}>
        {steps.map((step) => (
          <StepButtonDesktop key={step.step} active={step.step === 1}>
            <Typography variant="subtitle1" fontWeight={step.step === 1 ? 'bold' : 'medium'} sx={{ color: step.step === 1 ? '#fff' : '#444' }}>
              {step.label}
            </Typography>
            <Typography variant="body2" sx={{ color: step.step === 1 ? '#d0e6ff' : '#aaa' }}>
              {step.sublabel}
            </Typography>
          </StepButtonDesktop>
        ))}
      </StyledSidebarDesktop>

      {/* Main Content */}
      <StyledContent>
        <Container maxWidth="md" sx={{ mt: 4 }}> {/* Reduzi o marginTop do Container */}
          <Typography variant="h5" gutterBottom>
            1. Selecionar um Banco (Instituição Bancária)
          </Typography>
          <Typography variant="body1" color="text.secondary" gutterBottom sx={{ fontSize: '0.9rem' }}> {/* Reduzi o tamanho da fonte */}
            Selecione um banco para criar uma nova carta de VAN
          </Typography>

          {error && (
            <Alert severity="error" sx={{ mb: 2 }}> {/* Reduzi o marginBottom do Alert */}
              {error}
            </Alert>
          )}

          <Box sx={{ mt: 3 }}> {/* Reduzi o marginTop do Box */}
            <Select
              value={vanData.selectedBank?.id || ''}
              onChange={handleBankChange}
              displayEmpty
              fullWidth
              size="small" /* Tornar o select menor */
              sx={{ mb: 2, fontSize: '0.9rem' }} /* Reduzi a margem e a fonte */
            >
              <MenuItem value="" disabled>
                Selecionar banco
              </MenuItem>
              {banks.map((bank) => (
                <MenuItem key={bank.id} value={bank.id} sx={{ fontSize: '0.9rem' }}>
                  {bank.code} - {bank.name}
                </MenuItem>
              ))}
            </Select>

            <Box sx={{ display: 'flex', justifyContent: 'flex-end', mt: 3 }}> {/* Reduzi o marginTop do Box dos botões */}
              <Button
                variant="contained"
                onClick={handleNext}
                disabled={!vanData.selectedBank}
                size="small" /* Tornar o botão menor */
              >
                Próximo
              </Button>
            </Box>
          </Box>
        </Container>
      </StyledContent>
    </Box>
  );
}

export default BankSelection;
