import api from './api';
import { mockBankService } from './mock.service';

// Use o mock em ambiente de desenvolvimento ou se não houver API configurada
const useMock = true;

export const bankService = useMock ? mockBankService : {
  async getBanks() {
    const response = await api.get('/banks');
    return response.data;
  },

  async getBankById(id) {
    const response = await api.get(`/banks/${id}`);
    return response.data;
  },

  async getBankProducts(bankId) {
    const response = await api.get(`/banks/${bankId}/products`);
    return response.data;
  },
};
