import React, { useMemo, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Container,
  Box,
  Typography,
  Button,
  Paper,
  Card,
  CardContent,
  Divider,
  CircularProgress,
  Alert,
  useMediaQuery,
  useTheme,
} from '@mui/material';
import styled from 'styled-components';
import { useVan } from '../contexts/VanContext';
import AccountBalanceIcon from '@mui/icons-material/AccountBalance';
import SettingsIcon from '@mui/icons-material/Settings';
import AssignmentIcon from '@mui/icons-material/Assignment';
import CheckCircleIcon from '@mui/icons-material/CheckCircle';

const Sidebar = styled(Paper)<{ hidden: boolean }>`
  width: 250px;
  padding: 16px;
  display: ${({ hidden }) => (hidden ? 'none' : 'flex')};
  flex-direction: column;
  gap: 12px;
  height: calc(100vh - 64px);
  position: fixed;
  left: 0;
  top: 64px;
  background-color: #fafafa;
`;

const Content = styled(Box)`
  margin-left: 250px;
  padding: 32px;
  @media (max-width: 900px) {
    margin-left: 0;
    padding: 24px;
  }
`;

const StepButton = styled(Button)<{ active?: boolean }>`
  justify-content: flex-start;
  text-align: left;
  color: ${({ active }) => (active ? '#1976d2' : 'inherit')};
  background-color: ${({ active }) => (active ? '#e3f2fd' : 'transparent')};
  font-weight: ${({ active }) => (active ? 600 : 400)};
  gap: 8px;

  &:hover {
    background-color: #e3f2fd;
  }
`;

function ReviewData(): React.ReactElement {
  const navigate = useNavigate();
  const { vanData } = useVan();
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('md'));

  const backupData = useMemo(() => {
    try {
      const saved = localStorage.getItem('companyData');
      return saved ? JSON.parse(saved) : null;
    } catch {
      return null;
    }
  }, []);

  const formattedData = useMemo(() => {
    const data = vanData.companyData || backupData || {};
    return {
      empresa: {
        nome: data.razaoSocial || '[NOME DA EMPRESA]',
        cnpj: data.cnpj || '[CNPJ DA EMPRESA]',
      },
      responsavel: {
        nome: data.responsavelNome || '[NOME DO RESPONSÁVEL]',
        email: data.responsavelEmail || '[E-MAIL DO RESPONSÁVEL]',
        telefone: data.responsavelTelefone || '[TELEFONE DO RESPONSÁVEL]',
      },
      conta: {
        banco: vanData.selectedBank?.name || data.banco || '[BANCO]',
        agencia: data.agencia || '[AGÊNCIA]',
        conta: data.conta || '[CONTA]',
        convenio: data.convenio || '[CONVÊNIO]',
      },
      gerente: {
        nome: data.gerenteConta || '[NOME GERENTE]',
        email: data.gerenteEmail || '[EMAIL GERENTE]',
        telefone: data.gerenteTelefone || '[TELEFONE GERENTE]',
      },
    };
  }, [vanData, backupData]);

  const handleSubmit = async () => {
    setLoading(true);
    setError(null);
    try {
      const data = vanData.companyData || backupData;
      if (!data) throw new Error('Dados ausentes');
      localStorage.setItem('companyData', JSON.stringify(data));
      navigate('/success');
    } catch (err) {
      setError('Erro ao processar os dados.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <Box>
      <Box
        sx={{
          height: '64px',
          backgroundColor: 'primary.main',
          display: 'flex',
          alignItems: 'center',
          padding: '0 24px',
          position: 'fixed',
          top: 0,
          left: 0,
          right: 0,
          zIndex: 1100,
        }}
      >
        <img src="/tecno_branca.png" alt="Tecnospeed" style={{ height: '30px' }} />
      </Box>

      <Sidebar elevation={1} hidden={isMobile}>
        <StepButton startIcon={<AccountBalanceIcon />} onClick={() => navigate('/bank')}>
          Instituição bancária
        </StepButton>
        <StepButton startIcon={<SettingsIcon />} onClick={() => navigate('/products')}>
          Produtos
        </StepButton>
        <StepButton startIcon={<AssignmentIcon />} onClick={() => navigate('/company-data')}>
          Preenchimento de dados
        </StepButton>
        <StepButton startIcon={<CheckCircleIcon />} active>
          Conferir e validar
        </StepButton>
      </Sidebar>

      <Content>
        <Container maxWidth="md">
          <Typography variant="h5" gutterBottom>
            4. Conferir e validar informações da carta
          </Typography>
          <Typography variant="body1" color="text.secondary">
            Verifique todos os dados abaixo antes de confirmar.
          </Typography>

          {error && <Alert severity="error" sx={{ mt: 2 }}>{error}</Alert>}

          <Card sx={{ mt: 4 }}>
            <CardContent>
              <Typography variant="h6" gutterBottom align="center">
                Carta Autorização – Boletos
              </Typography>

              <Box sx={{ mt: 3, px: 1 }}>
                <Typography paragraph>
                  Ao BANCO <strong>{formattedData.conta.banco}</strong>
                </Typography>
                <Typography paragraph>A/C {formattedData.gerente.nome}</Typography>
                <Typography paragraph>Assunto: Intercâmbio de Arquivos – {formattedData.empresa.nome}</Typography>

                <Typography paragraph>
                  Comunicamos que nossa empresa passou a operar no relacionamento da EDI, transferência eletrônica de arquivos, através da VAN FINNET.
                </Typography>

                <Typography paragraph>
                  Solicitamos que esta Instituição disponibilize o suporte necessário para viabilizar esta implantação.
                </Typography>

                <Typography sx={{ mt: 2 }}>
                  <strong>Contratante:</strong><br />
                  Razão Social: {formattedData.empresa.nome}<br />
                  CNPJ: {formattedData.empresa.cnpj}<br />
                  Agência / Conta Corrente: {formattedData.conta.agencia} / {formattedData.conta.conta}<br />
                  Convênio: {formattedData.conta.convenio}
                </Typography>

                <Typography sx={{ mt: 3 }}>
                  <strong>Van Contratada:</strong><br />
                  FINNET S/A Tecnologia<br />
                  CNPJ: 03.607.206/0001-10
                </Typography>

                <Typography sx={{ mt: 3 }}>
                  <strong>Contato da Empresa:</strong><br />
                  {formattedData.responsavel.nome}<br />
                  {formattedData.responsavel.email}<br />
                  {formattedData.responsavel.telefone}
                </Typography>

                <Typography sx={{ mt: 3 }}>
                  <strong>Gerente Responsável:</strong><br />
                  {formattedData.gerente.nome}<br />
                  {formattedData.gerente.email}<br />
                  {formattedData.gerente.telefone}
                </Typography>

                <Typography paragraph sx={{ mt: 3 }}>
                  Colocamo-nos à disposição para quaisquer esclarecimentos adicionais.
                </Typography>

                <Typography paragraph>Atenciosamente,</Typography>

                <Box sx={{ mt: 4, textAlign: 'center' }}>
                  <Divider sx={{ width: '200px', margin: '0 auto' }} />
                  <Typography variant="body2" sx={{ mt: 1 }}>
                    Assinatura do Responsável
                  </Typography>
                </Box>
              </Box>
            </CardContent>
          </Card>

          <Box sx={{ display: 'flex', justifyContent: 'space-between', mt: 4 }}>
            <Button variant="outlined" onClick={() => navigate('/company-data')} disabled={loading}>
              Voltar
            </Button>
            <Button variant="contained" onClick={handleSubmit} disabled={loading}>
              {loading ? (
                <Box sx={{ display: 'flex', alignItems: 'center' }}>
                  <CircularProgress size={22} sx={{ mr: 1 }} color="inherit" />
                  Processando...
                </Box>
              ) : (
                'Confirmar e Enviar Carta'
              )}
            </Button>
          </Box>
        </Container>
      </Content>
    </Box>
  );
}

export default ReviewData;
