import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Container,
  Box,
  Typography,
  Select,
  MenuItem,
  Button,
  Paper,
  Alert,
  CircularProgress,
  SelectChangeEvent,
  IconButton,
  Menu,
} from '@mui/material';
import AccountCircle from '@mui/icons-material/AccountCircle';
import styled from 'styled-components';
import { bankService } from '../services/bank.service';
import { useVan } from '../contexts/VanContext';
import { Bank } from '../services/mock.service';

interface StepButtonProps {
  active?: boolean;
}

const StyledSidebar = styled(Paper)`
  width: 250px;
  padding: 16px;
  display: flex;
  flex-direction: column;
  gap: 8px;
  height: calc(100vh - 64px);
  position: fixed;
  left: 0;
  top: 64px;
`;

const StyledContent = styled(Box)`
  margin-left: 250px;
  padding: 24px;
`;

const StepButton = styled(Button)<StepButtonProps>`
  justify-content: flex-start;
  text-align: left;
  padding: 16px;
  background-color: ${(props) => (props.active ? '#e3f2fd' : 'transparent')};
  &:hover {
    background-color: #e3f2fd;
  }
`;

function BankSelection(): React.ReactElement {
  const navigate = useNavigate();
  const { vanData, updateVanData } = useVan();
  const [banks, setBanks] = useState<Bank[]>([]);
  const [loading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<string>('');

  // Estado para menu do ícone de conta
  const [anchorEl, setAnchorEl] = useState<null | HTMLElement>(null);

  useEffect(() => {
    loadBanks();
  }, []);

  const loadBanks = async (): Promise<void> => {
    try {
      const data = await bankService.getBanks();
      setBanks(data);
    } catch (err) {
      setError('Erro ao carregar a lista de bancos. Tente novamente.');
    } finally {
      setLoading(false);
    }
  };

  const handleBankChange = (event: SelectChangeEvent<string>): void => {
    const selectedBank = banks.find((bank) => bank.id === event.target.value);
    if (selectedBank) {
      updateVanData({ selectedBank });
    }
  };

  const handleNext = (): void => {
    if (vanData.selectedBank) {
      navigate('/product-selection');
    }
  };

  // Controle do menu do ícone de conta
  const handleMenu = (event: React.MouseEvent<HTMLElement>) => {
    setAnchorEl(event.currentTarget);
  };

  const handleClose = () => {
    setAnchorEl(null);
  };

  const handleLogout = () => {
    // Limpar sessão ou dados de usuário aqui, se precisar
    navigate('/login'); // Exemplo de redirecionamento para tela de login
  };

  if (loading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
        <CircularProgress />
      </Box>
    );
  }

  return (
    <Box>
      {/* Header */}
      <Box
        sx={{
          height: '64px',
          backgroundColor: 'primary.main',
          display: 'flex',
          alignItems: 'center',
          padding: '0 24px',
          position: 'fixed',
          top: 0,
          left: 0,
          right: 0,
          zIndex: 1000,
        }}
      >
        <img src="/tecno_branca.png" alt="Tecnospeed" style={{ height: '30px' }} />

        <Box sx={{ flexGrow: 1 }} />

        <IconButton
          size="large"
          aria-label="account of current user"
          aria-controls="menu-appbar"
          aria-haspopup="true"
          onClick={handleMenu}
          color="inherit"
        >
          <AccountCircle />
        </IconButton>

        <Menu
          id="menu-appbar"
          anchorEl={anchorEl}
          anchorOrigin={{
            vertical: 'top',
            horizontal: 'right',
          }}
          keepMounted
          transformOrigin={{
            vertical: 'top',
            horizontal: 'right',
          }}
          open={Boolean(anchorEl)}
          onClose={handleClose}
        >
          <MenuItem
            onClick={() => {
              handleClose();
              handleLogout();
            }}
          >
            Sair
          </MenuItem>
        </Menu>
      </Box>

      {/* Sidebar */}
      <StyledSidebar elevation={1}>
        <StepButton active variant="text" color="primary">
          Instituição bancária
        </StepButton>
        <StepButton variant="text" disabled>
          Produtos
        </StepButton>
        <StepButton variant="text" disabled>
          Preenchimento de dados
        </StepButton>
        <StepButton variant="text" disabled>
          Conferir e validar
        </StepButton>
      </StyledSidebar>

      {/* Main Content */}
      <StyledContent>
        <Container maxWidth="md" sx={{ mt: 8 }}>
          <Typography variant="h5" gutterBottom>
            1. Selecionar um Banco (Instituição Bancária)
          </Typography>
          <Typography variant="body1" color="text.secondary" gutterBottom>
            Selecione um banco para criar uma nova carta de VAN
          </Typography>

          {error && (
            <Alert severity="error" sx={{ mb: 3 }}>
              {error}
            </Alert>
          )}

          <Box sx={{ mt: 4 }}>
            <Select
              value={vanData.selectedBank?.id || ''}
              onChange={handleBankChange}
              displayEmpty
              fullWidth
              sx={{ mb: 3 }}
              renderValue={(selected) => {
                if (!selected) {
                  return <em>Selecionar banco</em>;
                }
                const bank = banks.find((b) => b.id === selected);
                return bank ? `${bank.code} - ${bank.name}` : '';
              }}
            >
              <MenuItem disabled value="">
                <em>Selecionar banco</em>
              </MenuItem>
              {banks.map((bank) => (
                <MenuItem key={bank.id} value={bank.id}>
                  {bank.code} - {bank.name}
                </MenuItem>
              ))}
            </Select>

            <Box sx={{ display: 'flex', justifyContent: 'space-between', mt: 4 }}>
              <Button variant="outlined" onClick={() => navigate('/')}>
                Voltar
              </Button>
              <Button variant="contained" onClick={handleNext} disabled={!vanData.selectedBank}>
                Próximo
              </Button>
            </Box>
          </Box>
        </Container>
      </StyledContent>
    </Box>
  );
}

export default BankSelection;
