import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Container,
  Box,
  Typography,
  Select,
  MenuItem,
  Button,
  Paper,
  Alert,
  CircularProgress,
  SelectChangeEvent,
} from '@mui/material';
import styled from 'styled-components';
import {
  Home as HomeIcon,
  Description as DescriptionIcon,
  Edit as EditIcon,
  CheckCircle as CheckCircleIcon,
} from '@mui/icons-material';
import { bankService } from '../services/bank.service';
import { useVan } from '../contexts/VanContext';
import { Bank } from '../services/mock.service';

// Estilização da Sidebar
const StyledSidebar = styled(Paper)`
  width: 250px;
  padding: 16px;
  display: flex;
  flex-direction: column;
  gap: 8px;
  height: calc(100vh - 64px);
  position: fixed;
  left: 0;
  top: 64px;
`;

const StyledContent = styled(Box)`
  margin-left: 250px;
  padding: 24px;
`;

const SidebarItem = styled(Box)<{ active?: boolean }>`
  display: flex;
  align-items: flex-start;
  padding: 16px;
  border-radius: 8px;
  background-color: ${({ active }) => (active ? '#003366' : '#f4f4f4')};
  color: ${({ active }) => (active ? '#fff' : '#333')};
  cursor: ${({ active }) => (active ? 'default' : 'pointer')};
  gap: 12px;

  &:hover {
    background-color: ${({ active }) => (active ? '#003366' : '#e0e0e0')};
  }

  svg {
    color: ${({ active }) => (active ? '#fff' : '#003366')};
    margin-top: 2px;
  }
`;

const SidebarText = styled(Box)`
  display: flex;
  flex-direction: column;

  span.title {
    font-weight: 600;
    font-size: 14px;
  }

  span.subtitle {
    font-size: 12px;
    color: inherit;
    opacity: 0.8;
  }
`;

function BankSelection(): React.ReactElement {
  const navigate = useNavigate();
  const { vanData, updateVanData } = useVan();
  const [banks, setBanks] = useState<Bank[]>([]);
  const [loading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<string>('');

  useEffect(() => {
    loadBanks();
  }, []);

  const loadBanks = async (): Promise<void> => {
    try {
      const data = await bankService.getBanks();
      setBanks(data);
    } catch (err) {
      setError('Erro ao carregar a lista de bancos. Tente novamente.');
    } finally {
      setLoading(false);
    }
  };

  const handleBankChange = (event: SelectChangeEvent<string>): void => {
    const selectedBank = banks.find(bank => bank.id === event.target.value);
    if (selectedBank) {
      updateVanData({ selectedBank });
    }
  };

  const handleNext = (): void => {
    if (vanData.selectedBank) {
      navigate('/product-selection');
    }
  };

  if (loading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
        <CircularProgress />
      </Box>
    );
  }

  return (
    <Box>
      {/* Header */}
      <Box
        sx={{
          height: '64px',
          backgroundColor: 'primary.main',
          display: 'flex',
          alignItems: 'center',
          padding: '0 24px',
          position: 'fixed',
          top: 0,
          left: 0,
          right: 0,
          zIndex: 1000,
        }}
      >
        <img src="/tecno_branca.png" alt="Tecnospeed" style={{ height: '30px' }} />
      </Box>

      {/* Sidebar */}
      <StyledSidebar elevation={1}>
        <SidebarItem active>
          <HomeIcon />
          <SidebarText>
            <span className="title">Instituição bancária</span>
            <span className="subtitle">Selecione uma instituição</span>
          </SidebarText>
        </SidebarItem>

        <SidebarItem>
          <DescriptionIcon />
          <SidebarText>
            <span className="title">Produtos</span>
            <span className="subtitle">Selecione um ou mais produtos</span>
          </SidebarText>
        </SidebarItem>

        <SidebarItem>
          <EditIcon />
          <SidebarText>
            <span className="title">Preenchimento de dados</span>
            <span className="subtitle">Selecione um ou mais produtos</span>
          </SidebarText>
        </SidebarItem>

        <SidebarItem>
          <CheckCircleIcon />
          <SidebarText>
            <span className="title">Conferir e validar</span>
            <span className="subtitle">Confirme os dados antes de enviar</span>
          </SidebarText>
        </SidebarItem>
      </StyledSidebar>

      {/* Main Content */}
      <StyledContent>
        <Container maxWidth="md" sx={{ mt: 8 }}>
          <Typography variant="h5" gutterBottom>
            1. Selecionar um Banco (Instituição Bancária)
          </Typography>
          <Typography variant="body1" color="text.secondary" gutterBottom>
            Selecione um banco para criar uma nova carta de VAN
          </Typography>

          {error && (
            <Alert severity="error" sx={{ mb: 3 }}>
              {error}
            </Alert>
          )}

          <Box sx={{ mt: 4 }}>
            <Select
              value={vanData.selectedBank?.id || ''}
              onChange={handleBankChange}
              displayEmpty
              fullWidth
              sx={{ mb: 3 }}
            >
              {banks.map((bank) => (
                <MenuItem key={bank.id} value={bank.id}>
                  {bank.code} - {bank.name}
                </MenuItem>
              ))}
            </Select>

            <Box sx={{ display: 'flex', justifyContent: 'flex-end', mt: 33 }}>
              <Button
                variant="contained"
                onClick={handleNext}
                disabled={!vanData.selectedBank}
              >
                Próximo
              </Button>
            </Box>
          </Box>
        </Container>
      </StyledContent>
    </Box>
  );
}

export default BankSelection;
